// parameter is configured to reference the value of the Space Slug field,
// which is defined in the Form Builder:
function validateSpaceSlug(slug) {
  K('bridgedResource[Current Spaces]').load({
    attributes: ['Space Slug'],
    values: {'Space Slug' : slug},
    success: function(bridgedData) {
      if (bridgedData === null || bridgedData.length === 0){
        K('field[Space Slug]').value(slug);
        K('button[Submit Button]').enable();
      } else {
        K('button[Submit Button]').disable();
        alert('A company with that identifier already exists. Please enter another.');
        K('field[Space Slug]').value(null);
        $(K('field[Space Slug]').element()).focus();
      }
    }
  });
}

